definition.script:
pipeline {
    agent any

    tools {
       go 'go1.18'
       nodejs 'nodejs'
       allure 'allure2.20'
       dockerTool 'docker'
    }

    stages {
        stage('GIT CLONE') {
            steps{
                git branch: 'hw3',
                url: 'https://github.com/adanil/sberDevops.git'
            }
        }
        stage('Build') {
            steps {
                dir('hw3') {
                  sh 'ls'
                  sh 'go build -o simpleServer main.go'
                }

            }
        }
        stage('Run tests') {
            steps {
                dir('hw3') {
                  sh 'ls'
                  sh './simpleServer &'
                }
                dir('hw3/tests') {
                  sh 'ls'
                  sh 'sleep 1s'
                  sh 'newman run hw3_tests.json -r allure'
                  sh 'ls'
                }

            }
        }
        stage('Generate allure report') {
            steps {
                script{
                           allure([
                          	   includeProperties: false,
                          	   jdk: '',
                          	   properties: [],
                          	   reportBuildPolicy: 'ALWAYS',
                          	   results: [[path: 'hw3/tests/allure-results']]
                    	   ])
                }
            }
        }
        stage('Sonar analyse') {
            environment {
              SCANNER_HOME = tool 'sonar-scanner'
            }
            steps {
            withSonarQubeEnv(credentialsId: 'sq_id', installationName: 'sq') {
                 sh '''$SCANNER_HOME/bin/sonar-scanner \
                 -Dsonar.projectKey=SBERhw3 \
                 -Dsonar.projectName=goserver \
                 -Dsonar.sources=hw3/main.go,hw3/server/server.go'''
               }
             }
        }
        stage('Build docker image') {
            steps {
                dir('hw3') {
                  sh 'ls'
                  script{
                    docker.build("hw3/server_image")
                  }
                }

            }
        }
    }
}
